import React, { Component } from 'react';
import 'isomorphic-fetch'; //We need this statement to use the Fetch API. You will also need to install isomorphic-fetch ('npm install --save isomorphic-fetch es6-promise') for
//this code to work when you write your own code with the Fetch API. This was already done when you 'npm install'ed this project
import './App.css';
import {Card, FormControl, InputGroup, Button} from 'react-bootstrap';

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            weather: []
        };

    this.fetchCityWeather = this.fetchCityWeather.bind(this);

    this.city = React.createRef();
    }
    componentDidMount() {
    }

    fetchCityWeather(){
        console.log("this.city", this.city);
        const city = (this.city)? "" : this.city.current.value;
        console.log("city", city);
        if(city){
            fetch(`api.openweathermap.org/data/2.5/weather?q=${city}`)
        .then(res => res.json())
        .then(
            (result) => {
                this.setState({
                    isLoaded: true,
                    weather: result.weather
                });
            },
            // Note: it's important to handle errors here  instead of a catch() block so that we don't swallow
            // exceptions from actual bugs in components.
            (error) => {
                this.setState({
                    isLoaded: true,
                    error
                });
            })
        }
    }

    _renderWeather(){
        const { error, isLoaded, weather } = this.state;
        if(weather.length === 0){
            return ""
        } else if (error) {
            return <div>Error: {error.message}</div>;
        } else if (!isLoaded) {
            return <div>Loading...</div>;
        } else if (weather.length > 0) {
            return (
                <Card style={{}}>
                    <Card.Header>{weather.name}</Card.Header>
                    <Card.Img variant="top" src="http://openweathermap.org/img/wn/09d.png@3x.png" />
                    <Card.Body>
                        <Card.Title>{weather.main}</Card.Title>
                        <Card.Text>{weather.description}</Card.Text>
                    </Card.Body>
                </Card>
            );
        } else {
            return "";
        }
    }

    render() {
     return(
        <div style={{ maxWidth: "500px", margin: "0 auto", padding:"50px"}}>  
            <link
            rel="stylesheet"
            href="https://maxcdn.bootstrapcdn.com/bootstrap/4.2.1/css/bootstrap.min.css"
            integrity="sha384-GJzZqFGwb1QTTN6wy59ffF1BuGJpLSa9DkKMp0DgiMDm4iYMj70gZWKYbI706tWS" crossOrigin="anonymous" />
            <InputGroup className="mb-3">
                <FormControl
                placeholder="City"
                aria-label="City"
                aria-describedby="city-addon"
                ref={this.city}
                />
                <InputGroup.Append>
                <Button variant="primary" type="button" onClick={this.fetchCityWeather}>Weather me!</Button>
                </InputGroup.Append>
            </InputGroup>
            {this._renderWeather()}
        </div>

     )
    }
 }


 export default App;
